import React, { Component } from "react";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { message: "" };
  }

  handleMessageChange = event => {
    this.setState({ message: event.target.value });
  };

  render() {
    const message = this.state.message,
      maxCharacterCount = 100,
      currentCharacterCount = message.length;

    return (
      <div>
        <h1>Tweeter</h1>

        <label for='entryform'>Text Form</label>
        <form name='entryform' id='entry-form'>
          <select name='usernames' id='usernames'>
            <option value='_bot1'>Bot1</option>
            <option value='_bot2'>Bot2</option>
            <option value='_bot3'>Bot3</option>
            <option value='_bot4'>Bot4</option>
            <option value='_bot5'>Bot5</option>
          </select>{" "}
          <br />
          <textarea
            value={message}
            onChange={this.handleMessageChange}
          ></textarea>
          <br />
          <span>Current Character Count: {currentCharacterCount}</span>
          <input type='submit' value='Submit'></input>
          <br />
        </form>
      </div>
    );
  }
}

export default App;
